import random as r
import json

def get_categories_url() -> any:
    """
    Return all categories that are product page lists.
    """
    try:
        with open("bricospider_categories.json") as file:
            data = json.load(file)
            dataset = []
            for row in data:
                if row["is_page_list"]:
                    dataset.append(row["url"])
            return dataset
    except:
        print("ERROR: No file found.")
    

